<?phpnamespace App\Models\Sistema\Providers;use App\Models\Sistema\Masters\Country;use App\Models\Sistema\Purchase\Purchase;use App\Models\Sistema\Solicitude\Solicitude;use App\Quotation;use DB;use Illuminate\Database\Eloquent\Collection;use Illuminate\Database\Eloquent\Model;use Illuminate\Database\Eloquent\SoftDeletes;class Provider extends  Model{    use SoftDeletes;    protected $table = 'tbl_proveedor';    ////soft deletes    protected $dates = ['deleted_at'];    ////foreing key    public function nombres_valcro()    {        return $this->hasMany('App\Models\Sistema\Providers\NombreValcro', 'prov_id');    }    public function address()    {        return $this->hasMany('App\Models\Sistema\Providers\ProviderAddress', 'prov_id');    }    public function contacts()    {        return $this->belongsToMany('App\Models\Sistema\Providers\Contactos', 'tbl_prov_cont', 'prov_id', 'cont_id');    }    public function contactsField(){        return $this->belongsToMany('App\Models\Sistema\Providers\Contactos', 'tbl_contacto_prov_campo', 'prov_id', 'cont_id')->withPivot('campo',"valor");    }    public function proveedor_product()    {        return $this->hasMany('App\Models\Sistema\Product\Product', 'prov_id');    }    /**     */    public function getProviderCoin(){        return $this->belongsToMany('App\Models\Sistema\Masters\Monedas', 'tbl_prov_moneda', 'prov_id','moneda_id')->withPivot('punto');    }    /**     * obtiene las cuentas bancarias del proveedor     */    public function bankAccount(){        return $this->hasMany('App\Models\Sistema\Providers\BankAccount','prov_id');    }    /**     * obtiene los limites de credito     */    public function limitCredit(){        return $this->hasMany('App\Models\Sistema\Providers\ProviderCreditLimit','prov_id');    }    /**     * obtiene los factores de conversion     */    public function convertFact(){        return $this->hasMany('App\Models\Sistema\Providers\ProviderFactor','prov_id');    }    /**     * obtiene los tiempos de produccion     */    public function prodTime(){        return $this->hasMany('App\Models\Sistema\Providers\ProdTime','prov_id');    }    /**     * obtiene los tiempos de produccion     */    public function transTime(){        return $this->hasMany('App\Models\Sistema\Providers\TiemAproTran','prov_id');    }    /**     * obtiene los Puntos configurado     */    public function points(){        return $this->hasMany('App\Models\Sistema\Providers\Point','prov_id');    }    /**     * obtiene las listas de precios     */    public function listPrice(){        return $this->hasMany('App\Models\Sistema\Providers\ProviderListPrice','prov_id');    }    /**     * @deprecated     * obtiene las direcciones de almacen de los proveedor     */    public function getAddress(){        return $this->hasMany('App\Models\Sistema\Providers\ProviderAddress','prov_id');    }    /**     * obtiene las condiciones de pago asignadas al proveedor     * @deprecated     */    public function getPaymentCondition(){        return $this->hasMany('App\Models\Sistema\Providers\ProviderCondPay','prov_id');    }    /**     * obtiene los pedidos     */    public function Order(){        return $this->hasMany('App\Models\Sistema\Order\Order','prov_id');    }    /**     * obtiene los contra pedidos     */    public function CustomOrder(){        return $this->hasMany('App\Models\Sistema\CustomOrders\CustomOrder','prov_id');    }    /**     * obtien la deuda de ese proveedor     * @deprecated     */    public function getFullPaymentOrders(){        return $this->getOrders()->sum('monto');    }    public function getDocuments(){        return $this->hasMany('App\Models\Sistema\Payments\DocumentCP','prov_id');    }    /**     * retorna los paises donde un proveedor tiene direciones    */    public function getCountry(){        $data = Collection::make(array());        $dirs = $this->address()->get();        foreach($dirs as $aux){            if(!$data->contains($aux->pais_id)){                $data->push(Country::find($aux->pais_id));            }        }        return$data;    }    /**     * retorna los paises donde un proveedor tiene direciones    */    public function country(){        $model = Country::selectRaw('distinct tbl_pais.id, short_name ')            ->join('tbl_prov_direccion','tbl_prov_direccion.pais_id','=','tbl_pais.id' )            ->where('tbl_prov_direccion.prov_id',$this->id);          //  ->get();        return $model;    }    public function solicitude(){        return $this->hasMany('App\Models\Sistema\Solicitude\Solicitude','prov_id');    }    public function purchase(){        return $this->hasMany('App\Models\Sistema\Purchase\Purchase','prov_id');    }    public function getOrderDocuments(){        $data= Collection::make(array());        $solic= $this->solicitude()->whereNotNull('final_id')->get();        $odc=  $this->purchase()->whereNotNull('final_id')->get();        $order=  $this->Order()->whereNotNull('final_id')->get();        $data->push($solic);        $data->push($odc);        $data->push($order);        $data=$data->collapse();        return $data;        //return $solic->get();    }    public function getTempOrderDocuments(){        $data= Collection::make(array());        $solic= $this->solicitude()->whereNull('final_id')->get();        $odc=  $this->purchase()->whereNull('final_id')->get();        $order=  $this->Order()->whereNull('final_id')->get();        $data->push($solic);        $data->push($odc);        $data->push($order);        $data=$data->collapse();        return $data;        //return $solic->get();    }}